(* % last_period solves for Vt in the last period of life for the given set of grid points and % for all possible states *)Print["Solving Period "<>ToString[LifeLength]];If[VerboseOutput == True,Print["Running last_period.m"]];LifePosn = LifeLength;EarliestPeriodSolved = EarliestEtVtp1Solved = LifePosn+1;If[SolutionMethod == Value,<<last_period_Value.m];If[SolutionMethod == FOC,  <<last_period_FOC.m];If[SolutionMethod == Both, <<last_period_Value.m;<<last_period_FOC.m];